export interface SparkPlugin {
    /**
     * Print log
     * @param options
     */
    echo(options: {
        value: string;
    }): Promise<string>;
    /**
     * Send discussion
     * @param options
     */
    sendDiscussion(options: {
        title: string;
        content: string;
    }): Promise<void>;
    /**
     * Choose member from native
     */
    memberPicker(): Promise<{
        members: string[];
    }>;
    /**
     * mapViewer
     */
    mapViewer(): Promise<void>;
    /**
     * sceneViewer
     */
    sceneViewer(): Promise<void>;
    /**
     * imageViewer
     */
    imageViewer(options: {
        urls: string[];
    }): Promise<void>;
    /**
     * videoPlayer
     */
    videoPlayer(): Promise<void>;
    /**
     * getSystemInfo
     */
    getSystemInfo(): Promise<{
        json: string;
    }>;
    /**
     * getUserInfo
     */
    getUserInfo(): Promise<{
        json: string;
    }>;
    /**
     * getAppContext
     */
    getAppContext(): Promise<{
        json: string;
    }>;
    /**
     * getAppConfig
     */
    getAppConfig(): Promise<{
        json: string;
    }>;
    /**
     * openSparkApp
     */
    openSparkApp(options: {
        appId: string;
        title: string;
    }): Promise<void>;
}
